import Groq from "groq-sdk";

const groq = new Groq({ 
  apiKey: process.env.GROQ_API_KEY || "default_key"
});

export async function getChatCompletion(
  messages: Array<{ role: "user" | "assistant" | "system"; content: string }>
): Promise<string> {
  try {
    // Using Groq's latest supported model for fast inference
    const response = await groq.chat.completions.create({
      model: "llama-3.3-70b-versatile",
      messages: [
        {
          role: "system",
          content: "You are a helpful AI assistant. Provide clear, concise, and helpful responses. When appropriate, use emojis to make your responses more engaging. Keep responses conversational and friendly."
        },
        ...messages
      ],
      max_tokens: 500,
      temperature: 0.7,
    });

    return response.choices[0].message.content || "I'm sorry, I couldn't generate a response.";
  } catch (error) {
    console.error("Groq API error:", error);
    throw new Error("Failed to get AI response");
  }
}

export async function analyzeUserIntent(message: string): Promise<{
  intent: string;
  entities: Record<string, any>;
  confidence: number;
}> {
  try {
    const response = await groq.chat.completions.create({
      model: "llama-3.3-70b-versatile",
      messages: [
        {
          role: "system",
          content: `Analyze the user's message and determine their intent. Respond with JSON in this format:
          {
            "intent": "weather|news|reminder|task|calendar|general",
            "entities": {
              "location": "string if mentioned",
              "time": "string if mentioned",
              "task": "string if task mentioned",
              "category": "string if news category mentioned"
            },
            "confidence": number between 0 and 1
          }`
        },
        {
          role: "user",
          content: message
        }
      ],
    });

    return JSON.parse(response.choices[0].message.content || "{}");
  } catch (error) {
    console.error("Intent analysis error:", error);
    return {
      intent: "general",
      entities: {},
      confidence: 0.5
    };
  }
}

server/services/reminders.ts.content:
import cron from "node-cron";

interface ScheduledReminder {
  id: string;
  task: string;
  scheduledTime: Date;
  cronJob?: any;
}

const scheduledReminders = new Map<string, ScheduledReminder>();

export function scheduleReminder(task: string, scheduledTime: Date): string {
  const id = `reminder_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  // Convert the scheduled time to a cron expression
  const cronExpression = dateToCron(scheduledTime);

  if (!cronExpression) {
    throw new Error("Invalid scheduled time");
  }

  try {
    const cronJob = cron.schedule(cronExpression, () => {
      console.log(`ðŸ”” Reminder: ${task}`);
      // In a real app, this would:
      // 1. Send a notification to the user
      // 2. Trigger a WebSocket event
      // 3. Update the reminder status in the database

      // Clean up the scheduled reminder
      scheduledReminders.delete(id);
    }, {
      timezone: "America/New_York" // Default timezone
    });

    const reminder: ScheduledReminder = {
      id,
      task,
      scheduledTime,
      cronJob
    };

    scheduledReminders.set(id, reminder);

    console.log(`ðŸ“… Scheduled reminder: ${task} at ${scheduledTime.toLocaleString()}`);
    return id;
  } catch (error) {
    console.error("Error scheduling reminder:", error);
    throw new Error("Failed to schedule reminder");
  }
}

export function cancelReminder(id: string): boolean {
  const reminder = scheduledReminders.get(id);
  if (!reminder) return false;

  if (reminder.cronJob) {
    reminder.cronJob.destroy();
  }

  scheduledReminders.delete(id);
  console.log(`âŒ Cancelled reminder: ${reminder.task}`);
  return true;
}

export function getScheduledReminders(): ScheduledReminder[] {
  return Array.from(scheduledReminders.values()).map(({ cronJob, ...reminder }) => reminder);
}

function dateToCron(date: Date): string | null {
  try {
    const minute = date.getMinutes();
    const hour = date.getHours();
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();

    // For one-time reminders, we create a cron that runs once
    // Format: "minute hour day month *"
    return `${minute} ${hour} ${day} ${month} *`;
  } catch (error) {
    console.error("Error converting date to cron:", error);
    return null;
  }
}

