      {/* Right Column - Hero Section */}
      <div className="hidden lg:flex lg:flex-1 bg-gradient-to-br from-primary to-primary/80 p-12 items-center justify-center">
        <div className="text-center text-primary-foreground max-w-lg">
          <h2 className="text-4xl font-bold mb-6">Experience AI Like Never Before</h2>
          <p className="text-lg mb-8 opacity-90">
            Chat naturally with your AI assistant using voice or text. Get weather updates, 
            manage tasks, set reminders, and access the latest news - all with the power of your voice.
          </p>

          {/* Feature highlights */}
          <div className="grid grid-cols-2 gap-6 mb-8">
            <div className="text-center">
              <div className="w-12 h-12 bg-primary-foreground/20 rounded-full flex items-center justify-center mx-auto mb-3">
                <Mic className="w-6 h-6" />
              </div>
              <h3 className="font-semibold mb-2">Voice Interaction</h3>
              <p className="text-sm opacity-80">Speak naturally and get voice responses</p>
            </div>

            <div className="text-center">
              <div className="w-12 h-12 bg-primary-foreground/20 rounded-full flex items-center justify-center mx-auto mb-3">
                <Brain className="w-6 h-6" />
              </div>
              <h3 className="font-semibold mb-2">Smart Assistant</h3>
              <p className="text-sm opacity-80">Powered by advanced AI technology</p>
            </div>

            <div className="text-center">
              <div className="w-12 h-12 bg-primary-foreground/20 rounded-full flex items-center justify-center mx-auto mb-3">
                <MessageSquare className="w-6 h-6" />
              </div>
              <h3 className="font-semibold mb-2">Rich Conversations</h3>
              <p className="text-sm opacity-80">Context-aware chat history</p>
            </div>

            <div className="text-center">
              <div className="w-12 h-12 bg-primary-foreground/20 rounded-full flex items-center justify-center mx-auto mb-3">
                <Users className="w-6 h-6" />
              </div>
              <h3 className="font-semibold mb-2">Personal Dashboard</h3>
              <p className="text-sm opacity-80">Manage tasks, reminders, and more</p>
            </div>
          </div>

          <p className="text-sm opacity-75">
            Join thousands of users who have transformed their productivity with VoiceAI
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/pages/home-page.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { useSpeechRecognition, useSpeechSynthesis } from "@/hooks/use-speech";
import { Conversation, Message } from "@shared/schema";
import { Sidebar } from "@/components/sidebar";
import { Dashboard } from "@/components/dashboard";
import { ChatMessage } from "@/components/chat-message";
import { VoiceModal } from "@/components/voice-modal";
import { NotificationToast } from "@/components/notification-toast";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Mic, 
  Send, 
  Menu,
  Activity,
  CloudSun,
  Newspaper,
  Bell,
  Calendar
} from "lucide-react";
import { useIsMobile } from "@/hooks/use-mobile";

export default function HomePage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isMobile = useIsMobile();

  const [currentConversationId, setCurrentConversationId] = useState<string | null>(null);
  const [messageInput, setMessageInput] = useState("");
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [notification, setNotification] = useState<{
    title: string;
    message: string;
  } | null>(null);

  const messagesEndRef = useRef<HTMLDivElement>(null);

  const {
    isListening,
    transcript,
    isSupported: speechSupported,
    startListening,
    stopListening,
  } = useSpeechRecognition();

  const { speak } = useSpeechSynthesis();

  // Fetch conversations
  const { data: conversations = [] } = useQuery<Conversation[]>({
    queryKey: ["/api/conversations"],
  });

  // Fetch messages for current conversation
  const { data: messages = [] } = useQuery<Message[]>({
    queryKey: ["/api/conversations", currentConversationId, "messages"],
    enabled: !!currentConversationId,
  });

  // Create new conversation mutation
  const createConversationMutation = useMutation({
    mutationFn: async (title: string) => {
      const res = await apiRequest("POST", "/api/conversations", { title });
      return await res.json();
    },
    onSuccess: (conversation: Conversation) => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      setCurrentConversationId(conversation.id);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create conversation",
        variant: "destructive",
      });
    },
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (data: { message: string; conversationId: string; isVoice: boolean }) => {
      const res = await apiRequest("POST", "/api/chat", data);
      return await res.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ 
        queryKey: ["/api/conversations", currentConversationId, "messages"] 
      });
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });

      // Speak the assistant's response
      if (data.shouldSpeak && data.assistantMessage?.content) {
        speak(data.assistantMessage.content);
      }
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to send message",
        variant: "destructive",
      });
    },
  });

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Handle voice transcript
  useEffect(() => {
    if (transcript && !isListening) {
      setMessageInput(transcript);
      setShowVoiceModal(false);
    }
  }, [transcript, isListening]);

  // Create initial conversation
  useEffect(() => {
    if (conversations.length === 0 && !createConversationMutation.isPending) {
      createConversationMutation.mutate("New Conversation");
    } else if (conversations.length > 0 && !currentConversationId) {
      setCurrentConversationId(conversations[0].id);
    }
  }, [conversations, currentConversationId]);

  const handleNewConversation = () => {
    const title = `Conversation ${conversations.length + 1}`;
    createConversationMutation.mutate(title);
  };

  const handleSelectConversation = (conversationId: string) => {
    setCurrentConversationId(conversationId);
    if (isMobile) {
      setIsSidebarOpen(false);
    }
  };

  const handleSendMessage = () => {
    if (!messageInput.trim() || !currentConversationId) return;

    sendMessageMutation.mutate({
      message: messageInput.trim(),
      conversationId: currentConversationId,
      isVoice: false,
    });

    setMessageInput("");
  };

  const handleVoiceInput = () => {
    if (!speechSupported) {
      toast({
        title: "Not Supported",
        description: "Speech recognition is not supported in your browser.",
        variant: "destructive",
      });
      return;
    }

    if (isListening) {
      stopListening();
    } else {
      setShowVoiceModal(true);
      startListening();
    }
  };

  const handleSendVoiceMessage = () => {
    if (!transcript.trim() || !currentConversationId) return;

    sendMessageMutation.mutate({
      message: transcript.trim(),
      conversationId: currentConversationId,
      isVoice: true,
    });

    setShowVoiceModal(false);
  };

  const handleQuickAction = (message: string) => {
    setMessageInput(message);
  };

  const quickActions = [
    {
      icon: CloudSun,
      title: "Weather",
      description: "Check today's forecast",
      message: "What's the weather today?",
    },
    {
      icon: Newspaper,
      title: "News",
      description: "Latest headlines",
      message: "Show me today's tech news",
    },
    {
      icon: Bell,
      title: "Reminder",
      description: "Schedule a task",
      message: "Set a reminder",
    },
    {
      icon: Calendar,
      title: "Calendar",
      description: "Manage events",
      message: "Add meeting to calendar",
    },
  ];

  return (
    <div className="flex h-screen overflow-hidden bg-background">
      {/* Sidebar */}
      <div className="hidden lg:block">
        <Sidebar
          onNewConversation={handleNewConversation}
          currentConversationId={currentConversationId || undefined}
          onSelectConversation={handleSelectConversation}
        />
      </div>

      {/* Mobile Sidebar */}
      <Sidebar
        onNewConversation={handleNewConversation}
        currentConversationId={currentConversationId || undefined}
        onSelectConversation={handleSelectConversation}
        isMobileOpen={isSidebarOpen}
        onMobileToggle={() => setIsSidebarOpen(!isSidebarOpen)}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Bar */}
        <div className="bg-card border-b border-border p-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="sm"
              className="lg:hidden"
              onClick={() => setIsSidebarOpen(true)}
              data-testid="button-toggle-sidebar"
            >
              <Menu className="w-4 h-4" />
            </Button>
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-accent rounded-full animate-pulse"></div>
              <span className="text-sm font-medium text-foreground">Assistant Active</span>
            </div>
          </div>

          {/* Voice Status Indicator */}
          <div className="flex items-center space-x-2 px-3 py-2 bg-muted rounded-lg">
            <Activity className="w-3 h-3 text-muted-foreground" />
            <span className="text-xs text-muted-foreground">
              {isListening ? "Listening..." : "Ready"}
            </span>
          </div>
        </div>

        {/* Chat Container */}
        <div className="flex-1 flex">
          {/* Messages Area */}
          <div className="flex-1 flex flex-col">
            <div className="flex-1 overflow-y-auto p-6 space-y-6" data-testid="messages-area">
              {messages.length === 0 ? (
                // Welcome Screen
                <div className="flex justify-center">
                  <div className="text-center max-w-2xl">
                    <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-4">
                      <Activity className="text-primary-foreground w-6 h-6" />
                    </div>
                    <h2 className="text-2xl font-semibold text-foreground mb-2">
                      Hi {user?.name || "there"}! I'm your AI assistant
                    </h2>
                    <p className="text-muted-foreground mb-6">
                      You can speak or type to interact with me. I can help you with weather, news, tasks, reminders, and much more!
                    </p>

                    {/* Quick Suggestions */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                      {quickActions.map((action, index) => (
                        <Button
                          key={index}
                          variant="outline"
                          className="p-4 h-auto text-left justify-start"
                          onClick={() => handleQuickAction(action.message)}
                          data-testid={`quick-action-${index}`}
                        >
                          <div className="flex items-center space-x-3">
                            <action.icon className="text-primary w-5 h-5" />
                            <div>
                              <p className="font-medium text-foreground">{action.title}</p>
                              <p className="text-sm text-muted-foreground">{action.description}</p>
                            </div>
                          </div>
                        </Button>
                      ))}
                    </div>
                  </div>
                </div>
              ) : (
                // Messages
                messages.map((message) => (
                  <ChatMessage key={message.id} message={message} />
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <div className="border-t border-border p-6">
              <div className="flex items-end space-x-3">
                <div className="flex-1">
                  <div className="relative">
                    <Textarea
                      value={messageInput}
                      onChange={(e) => setMessageInput(e.target.value)}
                      placeholder="Type your message or click the mic to speak..."
                      className="w-full resize-none pr-12"
                      rows={1}
                      onKeyDown={(e) => {
                        if (e.key === "Enter" && (e.ctrlKey || e.metaKey)) {
                          handleSendMessage();
                        }
                      }}
                      data-testid="input-message"
                    />
                    <Button
                      size="sm"
                      className="absolute right-3 top-3"
                      onClick={handleSendMessage}
                      disabled={!messageInput.trim() || sendMessageMutation.isPending}
                      data-testid="button-send-message"
                    >
                      <Send className="w-3 h-3" />
                    </Button>
                  </div>
                </div>

                {/* Voice Input Button */}
                <Button
                  size="lg"
                  className={`w-12 h-12 rounded-full ${
                    isListening ? "bg-red-500 hover:bg-red-600" : ""
                  }`}
                  onClick={handleVoiceInput}
                  disabled={!speechSupported}
                  data-testid="button-voice-input"
                >
                  <Mic className="w-5 h-5" />
                </Button>
              </div>

              <div className="mt-3 text-center">
                <p className="text-xs text-muted-foreground">
                  Try: "What's the weather?", "Set a reminder", "Show me news", or "Add to calendar"
                </p>
              </div>
            </div>
          </div>

          {/* Dashboard - Desktop Only */}
          <div className="hidden xl:block">
            <Dashboard />
          </div>
        </div>
      </div>

      {/* Voice Modal */}
      <VoiceModal
        isOpen={showVoiceModal}
        onClose={() => {
          setShowVoiceModal(false);
          stopListening();
        }}
        isListening={isListening}
        transcript={transcript}
        onToggleListening={handleVoiceInput}
      />

      {/* Notification Toast */}
      {notification && (
        <NotificationToast
          title={notification.title}
          message={notification.message}
          isVisible={!!notification}
          onDismiss={() => setNotification(null)}
          onMarkDone={() => {
            toast({
              title: "Task Completed",
              description: "Great job! Task marked as done.",
            });
            setNotification(null);
          }}
          onSnooze={() => {
            toast({
              title: "Reminder Snoozed",
              description: "I'll remind you again in 10 minutes.",
            });
            setNotification(null);
          }}
        />
      )}
    </div>
  );
}

